custom_css:
/*Bredcramabs*/
.page-heading a.back-history{
    letter-spacing:0;
}
#breadcrumb a,
.woocommerce-breadcrumb a{
    letter-spacing:0 !important;
    padding-right:8px !important;
}
.delimeter{
    padding-right:6px !important;
}
body{
    background-position: 160px top;
}
body .big-banner{
    background-position: 100px -70px !important;
}
body .big-banner-2{
    background-position: -120px !important;
}
body .big-banner-3{
    background-position: 400px !important;
}
body.home {
    background-image: url(https://xstore.8theme.com/demos/2/cryptocurrency/wp-content/uploads/sites/12/2018/04/home-bg.jpg);
    background-size: cover;
    background-position: center -80px;
}
.fixed-header{
    border-top:none;
}
.et-header-full-width .fixed-header .container{
    padding-top: .6em;
    padding-bottom: .6em;
}

.h1, .h2, .h3, .h4, .h5, .h6, h1, h2, h3, h4, h5, h6 {
    font-weight: 700 !important;
}
/*menu*/

.menu-wrapper .menu > li a{
    opacity: 0.8;
}
.fixed-header li a{
    text-decoration:none !important;
}
.menu-wrapper .menu > li a:hover,
.menu-wrapper .menu > li.current-menu-item > a{
    opacity: 1;
    text-decoration:none;
}
.header-color-white .menu-wrapper .menu > li.current-menu-item > a:after,
.header-color-white .menu-wrapper .menu > li > a:hover:after,
.header-color-dark .menu-wrapper .menu > li.current-menu-item > a:after {
    display:none;
}
/*shop*/
.single-product-booking .product-side-information-inner .yith-wcwl-add-to-wishlist a:not(.alt):before,
.product-content .compare:before{
    color: #794be5;
}
.product-share ul li a {
    padding: 3px 10px;
    color: #e9e9e9;
}
.product-share ul li a:hover {
    color: #76a9ea;
}
.single-product-booking .product-side-information-inner .product-share {
    text-align: left;
    border-top: 1px solid #e3e1e6;
    width: 70%;
    border-radius: 0;
    background-color: transparent;
    padding: 0;
    margin: 0 auto;
    border-bottom: none;
    border-right: none;
    border-left: none;
    padding-top: 15px;
}
.product_meta .posted_in{
    display:none !important;
}
.qty-span{
    text-transform: capitalize !important;
    font-size: 16px;
    font-weight: 400;
    color: #999999;
    margin-bottom: 7px;
    margin-top: 15px;
}
.quantity.buttons_added .minus:hover:before,
.quantity.buttons_added .plus:hover:before{
    color: #f2a02a;
}
.single-product-booking .product-side-information-inner .quantity.buttons_added span:hover {
    background-color: #fff;
}
.quantity.buttons_added.buttons_added input[type="number"] {
    margin-left: -1px;
    margin-right: -1px;
}
.single-product-booking .product-side-information-inner .quantity.buttons_added span, .single-product-booking .product-side-information-inner .quantity.buttons_added input[type="number"] {
    background-color: #fff;
    border: 1px solid #d1d1d1;
}
.single-product-booking .product-side-information-inner .product_meta .tagged_as a{
    color:#999;
}
.single-product-booking .product-side-information-inner .product_meta .tagged_as{
    margin-top:20px;
}
.single-product-booking .product-side-information-inner .compare,
.single-product .yith-wcwl-add-button a.add_to_wishlist,
.single-product-booking .product-side-information-inner .product_meta .tagged_as{
    font-size: 16px !important;
    letter-spacing: 0.5px;
    font-weight: 400 !important;
    color: #242424;
    text-transform:initial;
}
.single_add_to_cart_button.button {
    margin: 20px 0;
    text-transform: initial;
    font-size: 20px !important;
}
.single-product-booking .product-side-information-inner .qty-span {
    display: block;
}
.single-product .woocommerce-Price-currencySymbol {
    font-size:30px !important;
    padding-right:6px;
    position: relative;
    top: -25px;
}
.woocommerce-Price-currencySymbol{
    color: #efefef;
}
body .single-product-booking .product-side-information-inner .price .amount{
    font-size: 54px;
    color: #242424;
    font-family: 'Muli';
    font-weight: 700;
}
.single-product-booking .product-side-information-inner > div {
    background-color: #fff;
    padding: 40px 30px;
}
.product-side-information-inner{
    padding: 30px 30px 50px;
    border: 1px solid #e3e1e6;
    border-radius: 15px;
    margin-bottom:3vw;
    background:#fff;
}
.single-product .product_title.entry-title, .product-information-inner .title,
.product-side-information-inner .product-price-title{
    display:none !important;
}
/*blog*/
.et_load-posts .btn{
    background: #f09d24;
    width: 200px;
    height: 48px;
    line-height: 44px;
    border-radius: 30px;
    border-color:#f09d24;
    margin-bottom: 4vw;
}
.all-loaded.loaded{
    margin-bottom:4vw;
}
.single-post .menu-social-icons a{
    color: #e9e9e9;
    font-size: 22px;
    padding: 0 10px !important;
}
.single-post .menu-social-icons a:hover{
    color: #fa9c16 !important;
}
.single-post .share-title{
    font-size: 18px;
    color: #242424;
}
.single-post .post-heading {
    margin-bottom: 1.3em;
    margin-top: 80px;
}
.single-post .meta-post *{
    color:#aaa;
    font-size:18px;
    font-weight:400;
}
.single-post .content-article.entry-content p{
    font-size:18px;
    font-weight:400;
    line-height:1.6;
}
.et_load-posts .btn a {
    color: #fff;
    font-size: 16px;
    text-transform: initial;
    font-weight: 400;
}
a{
    text-decoration:none !important;
}
.single-post .share-post{
    border-top:none;
    margin-bottom: 4vw;
}
.wp-picture.blog-hover-zoom{
    border-radius: 15px 15px 0 0;
    margin-bottom:0 !important;
    background: #fff;
}
.single-post .entry-title, .post-categories,
.comment-respond, .menu-wrapper .menu > li > a:after, .header-color-white .menu-wrapper .menu > li > a:hover:after,
.header-color-dark .menu-wrapper .menu > li > a:hover:after {
    display:none !important;
}
 .grid-post-body{
    border-left: 1px solid #e3e1e6;
    border-right: 1px solid #e3e1e6;
    border-bottom: 1px solid #e3e1e6;
    border-radius: 0 0 15px 15px;
    padding: 30px 30px 40px;
    background:#fff;
}
.post-heading  h2 a{
    font-size: 18px;
    letter-spacing: 0.4px;
    color: #000;
    line-height: 1.55;
}
 .meta-post *{
    font-size:14px;
    font-weight:400;
    color:#aaa;
}
.content-article p {
    font-size: 14px;
    line-height: 1.7;
    font-weight: 400;
    color: #aaa;
}
 .post-heading {
    margin-bottom: 0;
}
 .content-grid .post-heading h2 {
    margin-bottom: 14px;
}
/*content*/
.mpc-button:hover{
    box-shadow: 0 5px 20px 7px #fde9cd;
    transition:0.2s !important;
}
.mpc-button{
    transition:0.2s !important;
}
.price-icon::before{
    content: "\0024";
    display: block;
    color: #efefef;
    font-size: 17px;
    position: absolute;
    right: 0;
    right: -16px;
    top: -8px;
}
.price-icon{
    display: inline-table;
    position:relative;
}

.letter-1{
    letter-spacing:1px;
}
.opacity-60 p{
    opacity:0.6;
}
.initial{
    text-transform:initial !important;
}
.letter-03{
    letter-spacing:0.3px;
}
/*footer*/
.hover p:hover{
    opacity:1 !important;
    cursor:pointer !important;
}
.et-follow-buttons.buttons-size-large a {
    width: auto;
    height: auto;
    line-height: initial;
    font-size: 26px;
    margin-right: 15px;
}
footer.footer .et-follow-buttons i {
    font-size: 18px !important;
}

@media only screen and (max-width: 1200px) {
    body section{
        padding-right:0px !important;
        padding-left:0px !important;
    }
    
    body .price-block > .vc_column-inner > .wpb_wrapper{
        padding: 10px 35px 30px !important;
    }
 
}


@media screen and (min-width: 1400px)  {
    body .crypto-banner.vc_col-sm-6{
        width:46% !important;
        margin-right:25px;
    }
}
@media only screen and (max-width: 480px) {
    .global-header-xstore .page-heading, .global-header-simple .page-heading {
       padding-top: 2em;
    }
    body .navbar-toggle {
       position: absolute;
       padding: 0;
       margin-top: 0px;
       margin-right: 0px;
       margin-bottom: 0px;
    }
    body .big-banner > .vc_row:first-child{
        padding-bottom: 70px !important;
    }
    .responsive-none{
        display:none !important;
    }
    .responsive-top{
        margin-top:100px;
        margin-bottom:0 !important;
    }
    body .smart{
        font-size: 47px !important;
        line-height: 50px !important;
        padding-top:0 !important;
    }
    body .second-block{
        padding-bottom:0 !important;
    }
    body {
       background-position: 40px 0px !important;
    }
    .responsive-heading{
        font-size:30px !important;
        line-height:30px !important;
    }
    
}
@media only screen and (max-width: 768px) {
    body .price-block > .vc_column-inner > .wpb_wrapper{
        padding: 10px 5px 30px !important;
    }
    home .grid-post-body {
        padding: 0px 10px 30px;
    }
    .story-block h2 {
       width: 70% !important;
       padding-left: 5px !important;
       margin-top: 0 !important;
    }
}

.fixed-header .navbar-header:empty {
    display:  none;
}
/* Blog hover with safari */
article.blog-post .wp-picture.blog-hover-zoom img, article.post-grid .wp-picture.blog-hover-zoom img, 
article.blog-post .wp-picture.blog-hover-zoom:hover img, article.post-grid .wp-picture.blog-hover-zoom:hover img {
    transform:  none;
}
