custom_css:
/*Shop*/
.single_add_to_cart_button.button:hover {
    border-color:#212121;
    background:#212121;
}
.single_add_to_cart_button.button{
    font-family: Poppins;
    font-weight: 400;
    background: #651fff;
    border-color: #651fff;
}
.product-information .price{
    color: #651fff;
    font-family: Playfair Display;
}
.arhive .page-heading,
.single-product .page-heading{
    background-image:url(https://xstore.8theme.com/demos/2/designers/wp-content/uploads/sites/11/2018/04/shop-breacrambs.jpg);
    background-size:cover;
    background-position:center center;
}

/* Blog */
.form-submit input[type="submit"] {
    background: #6520ff;
    border-color: #6520ff;
}

/*content*/
.page-heading {
    margin-bottom: 75px;
}
.head-after:after{
    display:block;
    content:" ";
    width:86px;
    height:2px;
    background:#651fff;
    margin-left: -83px;
    margin-top: -14px;
}
.vc_btn3.vc_btn3-color-blue:hover{
    background-color: #212121 !important;
}
.vc_btn3.vc_btn3-color-blue,
.vc_btn3.vc_btn3-color-blue.vc_btn3-style-flat {
    font-family: Poppins;
    font-size: 14px;
    font-weight: 400;
    color: #fff;
    background-color: #651fff;
    border-radius: 0;
    height: 50px;
    width: 174px;
}
.initial{
    text-transform:initial !important;
}
.height-300{
    height:300px;
}
.lowercase{
    text-transform:lowercase !important;
}
.letter-1{
    letter-spacing:1px !important;
}
.contain{
    background-size:contain !important;
}
/*Blog*/
.single-post .content-article.entry-content > div:first-child .wpb_text_column{
    margin-bottom:25px !important;
}
.form-submit input[type="submit"]:hover {
    background-color: #212121;
    border-color: #212121;
}
.etheme_portfolio-template-default .vc_separator.vc_sep_color_blue .vc_sep_line {
        border-color: #651fff !important;
}
 textarea.form-control::placeholder,
.comment-respond input[type=text]::placeholder,
.comment-respond input[type=email]::placeholder{
    color:#a3a3a3;
    font-size:16px;
}
 textarea.form-control:focus,
.comment-respond input[type=text]:focus,
.comment-respond input[type=email]:focus{
    border-bottom:2px solid #651fff !important;
}
.comment-respond input[type=text],
.comment-respond input[type=email]{
    height:44px;
}
textarea.form-control,
.comment-respond input[type=text],
.comment-respond input[type=email]{
    background: #fff !important;
    border-color: #fff !important;
    border-bottom-color: #f4f4f4 !important;
}
.single-post .post-heading h2{
    display:none;
}
.content-grid2:hover .meta-post-timeline .time-day,
.content-grid2:hover .meta-post-timeline .time-mon {
    color: #fff !important;
}
.et_count-posts,
.blog .content-grid2 .meta-post-timeline .time-mon,
.blog .time-day {
    font-family: "Playfair Display";
}
.blog .page-heading,
.single-post .page-heading{
    background-image:url(https://xstore.8theme.com/demos/2/designers/wp-content/uploads/sites/11/2018/04/blog-breacrambs.jpg);
    background-size:cover;
    background-position:center center;
}
.responsive-padding-bg-2 .mpc-button.mpc-transition.letter-1,
.price__section .mpc-button.mpc-transition.letter-1{
    overflow:visible;
}
.responsive-padding-bg-2 .mpc-button.mpc-transition.letter-1:hover::after,
.responsive-padding-bg-2 .mpc-button.mpc-transition.letter-1:hover::before,
.tp-caption.rev-btn:hover::after,
.vc_general.vc_btn3:hover::after,
.price__section .mpc-button.mpc-transition.letter-1:hover::after,
.tp-caption.rev-btn:hover::before,
.vc_general.vc_btn3:hover::before,
.price__section .mpc-button.mpc-transition.letter-1:hover::before{
    background:#212121;
}
.responsive-padding-bg-2 .mpc-button.mpc-transition.letter-1::after,
.tp-caption.rev-btn::after,
.vc_general.vc_btn3::after,
.price__section .mpc-button.mpc-transition.letter-1::after{
    display:block;
    content:" ";
    width:30px;
    height:2px;
    background:#651fff;
    position: absolute;
    top: 49%;
    left: -28px;
}
.responsive-padding-bg-2 .mpc-button.mpc-transition.letter-1::before,
.tp-caption.rev-btn::before,
.vc_general.vc_btn3::before,
.price__section .mpc-button.mpc-transition.letter-1::before{
    display:block;
    content:" ";
    width:30px;
    height:2px;
    background:#651fff;
    position: absolute;
    top: 49%;
    right: -28px;
}
.fixed-header{
    box-shadow: 1px 1px 1px #efefef;
}
/*footer*/
.footer-bottom{
    display:none;
}

footer.footer .vc_single_image-wrapper:hover{
    opacity:.6;
    transition:0.4s;
}
footer.footer .vc_single_image-wrapper{
    transition:0.4s;
}
.img-hover{
    transition: 0.5s !important;
}

@media only screen and (min-width: 1900px) {
    .logo-block .mpc-grid-images > .mpc-item{
       padding:0 45px;
    }
    body .responsive-padding-bg > .vc_column-inner{
        padding-right: 225px !important;
        padding-left: 225px !important;
    }
    body .responsive-padding-bg-2 > .vc_column-inner{
        padding-right: 270px !important;
        padding-left: 270px !important;
    }
}
@media only screen and (max-width : 1024px)  {
    body .responsive-padding-bg-2 > .vc_column-inner{
        padding-right: 15px !important;
        padding-left: 15px !important;
    }
    body .responsive-padding-bg > .vc_column-inner{
        padding-right: 60px !important;
        padding-left: 60px !important;
    }
    body div.cover.section-about-us{
        overflow: visible !important;
     }
     body .section-about-us{
         background-image:none !important;
     }
     .menu-element.horizontal .menu>li>a {
         padding: 0.5em .3em !important;
     }
     .price__section .price-text{
        font-size:16px !important;
     }
     .responsive-padding-bg-2 .text-center .wpb_wrapper > h2:first-child{
        font-size:18px !important;
    }
    footer.footer .lowercase.letter-1{
        font-size:50px !important;
    }
    .initial.know{
        font-size:56px !important;
    }
    .size-40{
        font-size:40px !important;
    }
    .size-60{
        font-size:54px !important;
    }
    .head-after{
        padding-left:90px !important;
    }
    .responsive-padding-abouts > .vc_column-inner{
        padding-left: 20px !important;
    }
    .single-product .product-information-inner {
        padding: 20px 0px 40px 0px;
    }
    .single-product .product-share {
        width: 180px;
    }
}

@media only screen and (min-device-width : 1024px) and (max-device-width : 1600px)  {
    .size-60{
        font-size:60px !important;
    }
    footer.footer .menu-element.horizontal .menu>li>a {
        padding: .77em .6em !important;
    }
    .responsive-padding-bg-2 .text-center .wpb_wrapper > h2:first-child{
        font-size:18px !important;
    }
    body .responsive-padding-bg-2 > .vc_column-inner,
    body .responsive-padding-bg > .vc_column-inner{
        padding-right: 100px !important;
        padding-left: 100px !important;
    }
    .price__section .price-text{
        font-size:20px !important;
    }
    .initial.know{
        font-size:56px !important;
    }
}

@media only screen and (min-width: 1600px){
    footer.footer > .container{
        width:1600px !important;
    }
}

