custom_css:
::selection{
    background:#e3d191;
}

/*header*/
.header .shopping-cart-widget{
    border-left: 1px solid #cecece;
    padding-left: 1.5em;
}
.menu-wrapper .menu > li > a{
    margin:0 15px;
}
.fixed-header {
    box-shadow: 0 0px 5px rgba(0, 0, 0, 0.3);
}
/*breadcrumbs*/
.back-history:hover{
    color:#e3d191 !important;
}
/*blog*/
.post-categories{
    display:none;
}
 .content article:nth-child(odd) .post-data,
 .content article:nth-child(even) .post-data,
 .grid-post-body,
 .et-blog .post-grid:nth-child(even):not(:first-of-type) .grid-post-body{
    padding:50px 40px 40px 40px;
    background:#fff;
    z-index: 2;
 }
 
 .content article:nth-child(odd) .post-data,
 .et-blog .post-grid:nth-child(even):not(:first-of-type) .grid-post-body,
 .grid-post-body{
    margin-left: -170px;
}

.content article:nth-child(even) .post-data {
    margin-right: -170px;
    text-align:right;
}
article.content-chess .wp-picture img{
    width:auto;
}

.home article:nth-child(even):not(:first-of-type) .wp-picture.blog-hover-zoom{
    width:585px !important;
}
.et-blog .post-grid .wp-picture, .et-blog .post-grid .post-gallery-slider, .et-blog .post-grid .featured-audio {
    width: 70%;
}
.et-blog .post-grid:nth-child(even):not(:first-of-type) .grid-post-body *{
    text-align:right;
}
.et-blog .post-grid:nth-child(even):not(:first-of-type) .read-more,
.content article:nth-child(even) .post-data .read-more{
   float:right;
   
}
.read-more{
    font-weight: 600;
    letter-spacing: 1px;
}
.posts-nav-btn .button:hover,
.posts-nav-btn:hover .button{
    background:#668eb8 !important;
}
.posts-nav-btn .button:before{
    color:#fff;
}
.et-blog .post-grid {
     width: 100% !important; 
}
.form-submit .submit ,
.single_add_to_cart_button.button,
.quick-view-info .product_type_variable{
    background: #e3d191 !important;
    border-color: #e3d191 !important;
}
.form-submit .submit:hover,
.single_add_to_cart_button.button:hover,
.quick-view-info .product_type_variable:hover{
    background:#d2bc6d !important;
    color:#fff !important;
    border-color:#d2bc6d !important;
}

/*Shop*/
.tab-content.tab-description .mpc-animation .wpb_column.vc_column_container{
    position: relative;
    overflow: hidden;
    height:295px;
    cursor: pointer;
    -webkit-transition: all 0.3s ease-in-out;
    transition: all 0.3s ease-in-out;
    backface-visibility: hidden;
    -webkit-backface-visibility: hidden;
    margin-left:15px;
}
.tab-content.tab-description .mpc-animation .wpb_column.vc_column_container .vc_column-inner{
     transition: all 0.4s ease-in-out;
     margin-left:0px !important;
}
.tab-content.tab-description .mpc-animation .wpb_column.vc_column_container .vc_column-inner:hover {
   -webkit-transform: scale(1.08);
    -ms-transform: scale(1.08);
    transform: scale(1.08);
    transition: all 0.4s ease-in-out;
}
.single.single-product .head-title::after {
    bottom: 5px;
}
.quick-view-popup .show-full-details {
     margin-top: 0px; 
}
.wc-tabs.tabs-nav .tab-title.opened span,
.wc-tabs.tabs-nav .tab-title:hover{
    color:#bfd0e3;
}
.wc-tabs.tabs-nav .tab-title{
    font-weight:500;
}
.wc-tabs.tabs-nav .tab-title span:after{
    display:none;
}
.single-product li:not(:last-child) .tab-title:before {
    content: "";
    width: 5px;
    height: 5px;
    background: #dddddd;
    display: block;
    position: absolute;
    right: 0;
    transform: translateY(-50%);
    top: 49%;
}
.wc-tabs.tabs-nav .tab-title span {
    padding: 0 20px 0 15px;
}
.variations .label label,
.single-product .qty-span,
.yith-wcwl-wishlistexistsbrowse.show a, .product-information .yith-wcwl-add-to-wishlist,
.compare.button{
    font-weight:500 !important;
}
.swiper-container.carousel-area{
    margin-bottom:4vw;
}
.single-product .products-title {
    margin-bottom: 40px;
}
.single.single-product .products-title span {
    font-weight:700 !important;
}
.product-information-inner .fixed-content h4.title{
    font-size:26px;
}
.product-information-inner .fixed-content .woocommerce-Price-amount{
    font-size:36px;
}
.sidebar-widget.woocommerce.widget_layered_nav,
.sidebar-widget.woocommerce.widget_product_categories{
    color:#bfbfbf !important;
}
.carousel-area .product-slide {
    padding: 0;
}
.switch-grid:hover a,
.switch-grid.switcher-active a,
.switch-list.switcher-active a,
.switch-list:hover a{
    color:#e3d191 !important;
}
.switch-list a,
.switch-grid a{
    color:#f2f8fd;
}
.price_label,
.price_label span{
    color:#e3d191 !important;
}

.archive .page-heading{
    margin-bottom:0 !important;
    border-bottom:none !important;
}
.price_slider_amount button.button:hover{
    background:#e3d191 !important;
    border-color:#e3d191 !important;
}
.woocommerce-Price-amount{
    color:#e3d191;
}

/*Content*/
input[type="text"]:focus,
input[type="email"]:focus,
input[type="password"]:focus,
textarea:focus{
    border-color:#e3d191 !important;
}
.fixed-header img{
       max-width:80px !important;
   }



.best-choice .mpc-button,
.button-see-all .mpc-button{
    letter-spacing:3px;
}

.head-title::after{
    margin-left: 30px;
    margin-right:32px;
    position: relative;
    bottom: 9px;
    content:url(https://xstore.8theme.com/demos/furniture2/wp-content/uploads/sites/49/2017/09/separator.png);
}
.head-title{
    font-family: "Open Sans" !important;
}


/*footer*/
.title-heading{
    font-size:30px;
    text-transform:uppercase;
    color:#1c1a1a;
    font-weight:700;
}
footer.footer .mpc-icon-column__description p{
    font-family:"Open Sans";
    font-weight:400;
    font-size:14px;
}
footer.footer .mpc-icon-column__description p strong{
    color:#1c1a1a;
    font-weight:400 !important;
}


.mpc-tabs__nav.mpc-align--left ul{
    margin-left:6%;
}
@media (min-width: 1700px){
footer.footer .container {
    width: 1700px;
}
}
@media (max-width: 768px){
    footer.footer div.vc_row.wpb_row{
        border-top:none !important;
    }
    .filter-wrap .woocommerce-ordering{
        padding-bottom: 0em;
    }
    .global-header-xstore.et-header-not-overlap .page-heading {
      padding-top: 1.7em;
      padding-bottom: 1.6em;
    }
    .header .shopping-cart-widget {
     border-left: none;
    padding-left: 0px !important; 
    }
   footer.footer *{
       text-align:center !important;
   }
   .footer-first-information {
       margin-left:0 !important;
   }
   footer.footer .mpc-icon-column--style_3 .mpc-icon{
       margin-bottom:-5px !important;
   }
   footer.footer .vc_row-has-fill>.vc_column_container>.vc_column-inner{
       padding-top:15px !important;
   }
   .title-heading {
    font-size: 24px;
    font-weight: 700 !important;
   }
   .block_info-contacts .wpb_column.vc_column_container{
       margin-bottom:50px;
   }
   .block_info-contacts .wpb_column.vc_column_container *{
       text-align:center !important;
   }
   body .block_info-contacts{
       margin-bottom:100px !important;
   }
   .info-about-us .mpc-icon-column{
       margin-bottom: 90px;
   }
   .fixed-header .container {
      padding-top: .6em;
      padding-bottom: .6em;
    }
   .info-about-us .mpc-icon-column__content {
     overflow: visible !important; 
   }
   .about-left-section > .vc_column-inner > .wpb_wrapper {
    margin-left: 0px !important; 
   }
   .none{
       display:none;
   }
   .content article:nth-child(odd) .post-data, .grid-post-body {
    padding: 20px;
    margin-left: 0px; 
    }
    article .post-data *{
        text-align:center;
    }
    .content article:nth-child(even) .post-data .read-more {
        float: none;
     }
     .page-heading h1.title{
         line-height: 35px;
         font-size:25px;
     }
     .home-section-right .wpb_single_image {
        margin-right: 0px !important; 
        margin-top:80px;
      }
      .home-section-left .wpb_single_image {
        margin-left: 0px !important;
        margin-top:60px;
      }
      .best-choice h2{
          font-size:29px !important;
      }
      .best-choice .vc_column-inner{
          margin-left:0 !important;
      }
      .mpc-tabs__nav.mpc-align--left ul {
         margin-left: 0%;
      }
       body .products-title{
          margin-bottom:0 !important;
          margin-top:60px;
      }
      .home .meta-post {
         font-size: 16px !important;
       }
       .button-see-all{
           margin-bottom:60px;
       }
       .home-section-left{
           margin-bottom:50px;
       }
       .home .content article:nth-child(odd) .post-data,
       .home .grid-post-body {
           margin-left: 0px !important; 
        }
}


@media screen and (min-width: 1900px) {
   .mpc-tabs__nav.mpc-align--left ul{
    margin-left:15%;
}
}
