custom_css:
/*top-bar*/
.top-bar {
    border-bottom: none;
}

/*header*/
header .navbar-header {
    display:none !important;
}
.fixed-header .header-logo img{
    max-width:50px !important;
}
.header-double-menu .menu-wrapper .menu > li {
    padding: 0px 15px;
    border-right: 1px solid #cb4d55;
}
.header-double-menu .menu-wrapper .menu > li:hover {
    border-right-style: solid;
}

.header-wrapper.header-double-menu .container-wrapper {
    border-bottom:none;
}

/*blog*/

.author-info .media-heading {
    text-transform: uppercase;
}
.blog .btn-read-more:hover{
    background: linear-gradient(to bottom, #a90c15 100%,#da3d46 100%,#da3d46 100%,#2989d8 100%,#2989d8 100%,#2989d8 100%,#000000 100%) !important;
}
.blog .btn-read-more{
    border-radius: 50px;
    font-size: 18px;
    font-weight: 500;
    border: none !important;
    background: linear-gradient(to bottom, #a90c15 0%,#da3d46 100%,#da3d46 100%,#2989d8 100%,#2989d8 100%,#2989d8 100%,#000000 100%);
}
.home .blog-mask{
    display:none !important;
}
.read-more.btn:hover{
   background: linear-gradient(to bottom, #a90c15 100%,#da3d46 100%,#da3d46 100%,#2989d8 100%,#2989d8 100%,#2989d8 100%,#000000 100%) !important;
}
.wp-picture .post-categories{
    border-radius:0 20px 20px 0;
    background: linear-gradient(to bottom, #a90c15 0%,#da3d46 100%,#da3d46 100%,#2989d8 100%,#2989d8 100%,#2989d8 100%,#000000 100%);
}
.entry-date.published.updated:before,
.single-product .product-information-inner .woocommerce-Price-amount,
.quick-view-info .woocommerce-Price-amount{
background:
    -webkit-linear-gradient(top, rgba(169,12,20,1) 0%, rgba(207,80,91,1) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    color:#B7252E;
}
.read-more.btn {
    font-size: 16px;
    border:none;
    border-radius: 40px;
    background: linear-gradient(to bottom, #a90c15 0%,#da3d46 100%,#da3d46 100%,#2989d8 100%,#2989d8 100%,#2989d8 100%,#000000 100%);
}

/* widgets overlay */
.quick-view-popup .quick-view-excerpts .excerpt-content:before {
    background: linear-gradient(to top, rgba(11, 16, 20, 0) 0%, #0b1014 80%);
}
.archive.woocommerce-page.s_widgets-with-scroll .sidebar .sidebar-widget:not(.sidebar-slider) > ul:after, 
.archive.woocommerce-page.s_widgets-with-scroll .shop-filters .sidebar-widget:not(.sidebar-slider) > ul:after, 
.archive.woocommerce-page.s_widgets-with-scroll .sidebar .sidebar-widget:not(.sidebar-slider) > div:after, 
.archive.woocommerce-page.s_widgets-with-scroll .shop-filters .sidebar-widget:not(.sidebar-slider) > div:after, 
.quick-view-popup .quick-view-excerpts .excerpt-content:after {
    background: linear-gradient(to bottom, rgba(11, 16, 20, 0) 0%, #0b1014 80%);
}

/*Shop*/

body .shop_table.woocommerce-checkout-review-order-table *{
    border-color:#2f2f2f !important;
}
.checkout_coupon input.btn{
    border:none;
}
.button.btn-checkout.wc-forward,
.wc-proceed-to-checkout a,
.form-row.place-order input,
.btn.remove-item:hover,
.checkout_coupon input.btn:hover,
.button.btn-view-wishlist,
.remove.remove_from_wishlist:hover,
.woocommerce-Button.button,
.cart-empty.empty-cart-block .btn{
   border:none !important;
   background: linear-gradient(to bottom, #a90c15 0%,#da3d46 100%,#da3d46 100%,#2989d8 100%,#2989d8 100%,#2989d8 100%,#000000 100%);
}

.form-submit .submit:hover,
.product-information-inner .add_to_cart_button:hover{
    background: linear-gradient(to bottom, #a90c15 100%,#da3d46 100%,#da3d46 100%,#2989d8 100%,#2989d8 100%,#2989d8 100%,#000000 100%) !important;
}
.form-submit .submit,
.product-information-inner .add_to_cart_button,
.cart .add_to_cart_button{
    border-radius:30px;
    font-weight:500;
    border:none;
    background: linear-gradient(to bottom, #a90c15 0%,#da3d46 100%,#da3d46 100%,#2989d8 100%,#2989d8 100%,#2989d8 100%,#000000 100%);
}


td ,
tr,
th,
.shop_table,
.block-title,
.woocommerce-billing-fields .step-title,
.woocommerce-shipping-fields .step-title,
.woocommerce table.wishlist_table tbody td,
.woocommerce-wishlist.woocommerce div.page-wrapper table.wishlist_table tbody td *,
.wishlist-dropdown.product_list_widget *,
.cart-popup-container *,
.woocommerce-account header.title,
#customer_login .col-1,
#customer_login h2,
.woocommerce-account .woocommerce h2{
   border-color:#261f1f !important;
}

.content-product.product-inview:hover .product-image-wrapper img{
    -webkit-filter: grayscale(100%); /* Safari 6.0 - 9.0 */
    filter: grayscale(100%);
}
.product-details .products-page-cats a{
    color: #da3d46;
    letter-spacing: 1px;
}
.input-text{
    margin:0 !important;
}
.after-shop-loop .woocommerce-result-count{
    display:none;
}
.woocommerce-pagination ul li a, .woocommerce-pagination ul li span{
    border:none;
    background:transparent;
    font-weight:200;
}
.single-product .tab-title:before {
    content: "";
    width: 6px;
    height: 6px;
    background: #261f1f;
    display: block;
    position: absolute;
    right: 0;
    transform: translate(100%, -50%);
    top: 50%;
}
.single-product li:last-child a.tab-title::before,
.wc-tabs.tabs-nav .tab-title span:after{
    display:none;
}

/*footer*/
.footer .mc4wp-form .et-mailchimp, 
.prefooter .mc4wp-form .et-mailchimp {
    margin-bottom: 10px;
}

.footer-mailchimp-text{
    margin-bottom:8px;
    font-size:14px;
    font-weight:400;
}

.null-instagram-feed .instagram-pics.instagram-columns-4 li {
    width: 23%;
    padding: 0 5px 10px 0;
}
footer.footer .wpb_widgetised_column.wpb_content_element{
    margin-bottom: 26px;
}
footer.footer input[type="email"]::-webkit-input-placeholder {
    color: #a4a4a4;
}

@media only screen and (min-width: 1900px) {
    .page-heading{
      background-position: 50% 100% !important;
    }
}

@media only screen and (max-width:992px) {
    .subscribe-block .null-instagram-feed .instagram-pics li {
        padding: 0.1em !important;
    }
}

