custom_css:
/*Header*/
.navbar-header.show-in-header{
    width:125px !important;
}
.header-xstore .menu-wrapper, .header-xstore .header-logo, .header-xstore .navbar-header {
    width: auto;
    
}
header.header ul li,
.fixed-header ul li{
    padding:0 24px !important;
}

/*Content*/
.hover-bg .vc_column-inner{
    transition: all 0.4s ease-in-out
}
.hover-bg:hover .vc_column-inner {
    -webkit-transform: scale(1.05);
    -ms-transform: scale(1.05);
    transform: scale(1.05);
    transition: all 0.4s ease-in-out;
}
.hover-bg{
    position: relative;
    overflow: hidden;
    cursor: pointer;
    -webkit-transition: all 0.3s ease-in-out;
    transition: all 0.3s ease-in-out;
    backface-visibility: hidden;
    -webkit-backface-visibility: hidden;
}
.capitalize{
    text-transform:initial !important;
}
.letter-1{
    letter-spacing:1px;
}

/*Shop*/
.woocommerce-wishlist footer.footer {
    position:fixed !important;
    bottom:0 !important;
}

.single-product .single_add_to_cart_button,
.form-submit input[type=submit]{
    font-family: "Josefin Sans";
    font-weight:100 !important;
    text-transform:capitalize !important;
    height:50px;
    width:220px;
    font-size:18px;
    border-radius:60px;
    margin-top: 20px;
}
 .single-product .single_add_to_cart_button{
    margin-top:0px !important;
}
.small-h,
.woocommerce-mini-cart > p{
    font-family: "Josefin Sans";
    font-weight:400;
}
.single-post input[type=email],
.single-post input[type=text],
.single-post textarea{
    background-color: transparent;
    border-top:none !important;
    border-left:none !important;
    border-right:none !important;
    border-bottom: 1px solid #e0e2e3;
    font-size:16px;
    color:#6e6e6e;
    font-weight:300;
    padding-left:20px;
    width:100% !important;
}
.single-post .comment-form input:focus,
.single-post .comment-form textarea:focus{
     border-bottom: 2px solid #302e2e;
}
.single-post .form-submit{
    text-align:center !important;
    margin-bottom:60px;
}
.product-information table.variations td.label label,
.product-content .compare,
.single-product .product-information .yith-wcwl-add-to-wishlist a:not(.alt),
.single-product .qty-span{
    font-family: "Josefin Sans";
    font-size:18px;
    font-weight:400;
    text-transform:capitalize;
}
.single-product-wide .product-information-inner ins .woocommerce-Price-amount.amount {
    font-weight: 400;
}
.single-product .product-information .price{
     font-family: "Josefin Sans";
     font-weight:400;
}
.single-product .product-information .price del .amount{
    font-size:25px !important;
   
}
.page-heading .title{
    text-transform:capitalize !important;
}
.products-grid .content-product .price del,
.products-grid .content-product .price ins{
    font-weight:400 !important;
}
.products-grid .content-product .price {
    font-family: "Josefin Sans";
}
.single-product-wide .product-information-inner .product_title{
     font-family: "Josefin Sans";
     font-weight:400;
}
.product-details .product-title a{
    font-family: "Josefin Sans";
    font-size: 20px;
}
/*blog*/

.page-heading h1::first-letter{
    text-transform: uppercase !important;
}
.page-heading h1 span{
    text-transform: lowercase;
}
.single-post article.blog-post{
     margin-bottom: 0px; 
}
.blog article p{
    font-family: Montserrat;
}
.first-block .vc_column-inner{
    height:1040px;
}
.post-comments-count span,
.views-count{
    color:#c0c0c0 !important;
}
.views-count:before{
    color:#302e2e;
}
.post-heading h2 a{
    text-transform: lowercase;
}
.post-heading h2{
    font-size:24px !important;
    line-height:1.3em;
    letter-spacing:0;
    font-family: "Josefin Sans";
    
}
.single-post .post-heading{
    display:none;
}
.post-heading h2::first-letter{
    text-transform: uppercase !important;
}
.blog article *{
   text-align:center !important;
}
.read-more{
    margin:0 auto;
    font-family: "Josefin Sans";
    font-weight:700;
}

/*Footer*/
footer.footer ul li a{
    font-size:14px !important;
    margin:0px 23px !important; 
   text-transform: uppercase;
}
footer.footer .menu-main-menu-container ul.menu li a{
    display: inline-block;
    color:#302e2e !important;
    line-height: 26px;
    text-decoration:none;
    cursor: pointer;
    position:relative;
    font-weight:600;
}
footer.footer .menu-main-menu-container ul.menu li a::after{
    background-color: #302e2e;
    display: block;
    content: "";
    height: 2px;
    width: 0%;
    left:50%;
    position:absolute;
    -webkit-transition: width .3s ease-in-out;
    -moz--transition: width .3s ease-in-out;
    transition: width .3s ease-in-out;
    -webkit-transform:translateX(-50%);
    -moz-transform:translateX(-50%);
    transform:translateX(-50%);
}
footer.footer .menu-main-menu-container ul.menu li.current_page_item a::after,
footer.footer .menu-main-menu-container ul.menu li a:hover::after{
    width: 100%;
}

footer.footer {
    position:relative;
    z-index:20;
}
@media screen and (max-width: 768px) {
    .woocommerce-cart table.cart .product-details a{
        font-size: 11px;
        line-height: 1.5em;
        margin-bottom: -4px;
    }
    .page-heading .title{
        font-size:38px !important;
        line-height:40px !important;
    }
    .meta-post {
      font-size: 15px;
     }
    .ipad-responsive{
        width:50% !important;
    }
    .home-section-1 .vc_column-inner, .home-section-2 .vc_column-inner {
       max-height: 300px !important;
    }
    
    .single-product .col-lg-6.col-md-6.col-sm-12.product-images {
       margin-bottom: 0px !important;
     }
     .single-product .single_add_to_cart_button {
     margin-top: 15px !important; 
   }
   .comment-reply-title {
    font-size: 32px;
    }

    footer.footer ul li a {
       margin: 0px 15px !important;
     }
    .mobile-menu-wrapper .header-search.act-default .search-btn i {
       line-height: 1.4;
       padding-right: 5px;
       overflow: visible; 
    }
    .black-block .mpc-list__title{
        text-align:left !important;
    }
    .vc_separator{
        display:none !important;
    }
    .black-block .vc_column-inner{
        padding:60px 0 20px;
    }
    .white-block .vc_column-inner,
    .black-block .vc_column-inner {
        padding-right: 15px !important;
        text-align:center;
        padding-left: 15px !important;
    }
    .about-us-section .about-us-right .vc_column-inner {
        padding-top: 140px !important;
    }
    .about-us-section h2{
        font-size:38px !important;
    }
    .white-block h2,
    .white-block .wpb_text_column p{
        text-align:center !important;
    }

     body .template-container  .sidebar-position-without .first-block .vc_column-inner,
     body .template-container  .sidebar-position-without .two-block .vc_column-inner,
     body .template-container  .sidebar-position-without .three-block .vc_column-inner{
       max-height: 500px !important;
     }
    .about-us-section * {
        text-align:center !important;
    }
    .about-us-section .vc_column-inner {
        padding-top: 340px !important;
        padding-right: 15px !important;
        padding-left: 15px !important;
    }
   .home .wpcf7-form-control.wpcf7-submit{
        margin-left:15px !important;
    }
    .home .wpcf7{
        text-align:center !important;
    }
    body .block-informations .et-follow-buttons.buttons-size-normal {
       margin-left: 0px !important;
       margin-bottom:60px;
    }
   body  .section_info{
        padding-left:30px !important;
        padding-right:30px !important;
    }
    footer.footer *{
        text-align:center !important;
    }
    .footer .footer-widgets ul li {
      padding:0;
     }
     .menu-element.horizontal.menu-align-center{
         margin-bottom:30px;
     }
    body .responsive-text *,
    .responsive-text{
        text-align:center !important;
    }
    footer.footer ul li a{
        color:#302e2e !important;
    }
    .responsive-margin-bottom,
    .white-block .mpc-button{
        margin-bottom:50px;
    }
    .page-heading{
        padding-top: 7em !important;
       padding-bottom: 2em !important;
    }
}
@media screen and (max-width: 768px) {
    .ipad-responsive{
        width:100% !important;
    }
}
@media screen and (min-width: 1700px) {
    .blog article p{
      padding: 0px 40px;
    }
}
@media screen and (min-width: 1900px) {
   body .block-informations .vc_column-inner{
       padding-left:200px !important;
   }
}
@media screen and (min-width: 2100px) {
   body .block-informations .vc_column-inner{
       padding-left:300px !important;
   }
}
