custom_css:
::selection {background: #9f3659;}
/*Header*/
body .page-heading {
    margin-bottom:30px;
    box-shadow: 1px 1px 38px #f6f6f6 inset;
}

/* woocommerce info section */
.woocommerce-info, .dokan-info, .mc4wp-info {
    border-top-color: #9f3659;
}

.revolution-section{
    box-shadow: 1px 1px 40px #f6f6f6 inset;
    border-top:1px solid #eeedee;
    border-bottom:1px solid #eeedee;
}
.slider-container .title{
    text-align:left;
    font-size:19px !important;
}
.fullscreen-menu .hamburger-icon i{
    color: #222;
}
.fullscreen-menu{
    background:rgba(255, 255, 255, 0.9) !important;
}
.fullscreen-menu .menu > li > a:hover{
    color:#9f3659 !important;
}
.fullscreen-menu .menu > li > a{
    color:#231f20 !important;
    font-family:Lora;
}
.media-heading,
.author-desc,
.vcard{
    text-align:left !important;
}

body .custom-bg-product .mpc-image .mpc-item,
body .custom-bg-product .mpc-image{
    overflow:visible !important;
}
.custom-bg-product:hover img{
       -webkit-transform: scale(1.06);
    -ms-transform: scale(1.06);
    transform: scale(1.06);
    transition: all 0.4s ease-in-out;
}
.woocommerce-cart .page-heading{
    background-image: url(https://xstore.8theme.com/demos/makeup/wp-content/uploads/sites/39/2017/06/cart.jpg) !important;
    background-position:center center;
}
.archive .page-heading{
    background-image: url(https://xstore.8theme.com/demos/makeup/wp-content/uploads/sites/39/2017/06/Shop.jpg) !important;
    background-position:center center;
}
.single-product .page-heading {
    background-image: url(https://xstore.8theme.com/demos/makeup/wp-content/uploads/sites/39/2017/06/Product-page.jpg) !important;
    background-position:center center;
}
.woocommerce-wishlist .page-heading {
    background-image: url(https://xstore.8theme.com/demos/makeup/wp-content/uploads/sites/39/2017/06/wishlist.jpg) !important;
    background-position:center center;
}
.woocommerce-account .page-heading {
    background-image: url(https://xstore.8theme.com/demos/makeup/wp-content/uploads/sites/39/2017/06/Account.jpg) !important;
    background-position:center center;
}
.woocommerce-tabs li a span{
    text-transform:capitalize;
}
.woocommerce-tabs li a {
    padding:5px 20px !important;
    margin:0 20px;
    font-family:Lora;
    font-weight:400 !important;
}
.woocommerce-tabs li a:hover,
.woocommerce-tabs li.active.et-opened a{
   background:#ffeae5;
}
.single-product h2.products-title {
    padding-bottom: 65px;
    margin-bottom: 40px;
    background-image: url(https://xstore.8theme.com/demos/makeup/wp-content/uploads/sites/39/2016/05/img-subtitle.png);
    background-repeat: no-repeat;
    background-position: center bottom;
}
.single-product .woocommerce-tabs .vc_row{
    background-image:none !important;
}
.tp-bullet-title{
    display:none;
}
.single-product .carousel-area .products-title span {
    font-size: 40px;
    font-weight: 400;
    text-transform: none;
}
.single-product .carousel-area{
    margin-bottom:5vw;
}
.shop-filters-area .widget-title {
    text-transform:uppercase;
    color:#231f20;
}

.fixed-product-block .fixed-content .menu-social-icons li a:hover {
    color: #9f3659 !important;
}
.product-title,
.product-title a{
    text-transform:uppercase !important;
}
/*Content*/

.home .mpc-product__thumb-wrap{
    display:none !important;
}
.product-btn{
    padding:25px 65px !important;
}
.single-product .fixed-content .woocommerce-Price-amount{
    font-size:40px;
    font-family:Lora;
    font-weight:400;
}
.custom-button:hover{
    border:none;
    width:311px;
    cursor:pointer;
    background-color:transparent !important;
}
.meta-post-timeline{
    width: 116px !important;
    height: 89px !important;
    border: none !important;
    background:url(https://xstore.8theme.com/demos/makeup/wp-content/uploads/sites/39/2017/05/pomada.png) !important;
}
.meta-post-timeline .time-day{
    color:#fff !important;
    font-family:Lora;
}
.shop-full-width .filter-wrap .filter-content{
    border:none !important;
}
.meta-post-timeline .time-mon{
    color:#fff !important;
     font-family:Lora;
    margin-top: -5px !important;
}
.single-post .grid-post-body h2{
    text-align:center;
}
.section-home-galery .custom-button{
    padding: 31px 104px !important;
}
.single-product .single_add_to_cart_button:hover,
.custom-button:hover,
.wpcf7-form input[type=submit]:hover{
    background: url(https://xstore.8theme.com/demos/makeup/wp-content/uploads/sites/39/2017/06/black-img.png) no-repeat;
    background-color: transparent !important;
}
.custom-button,
.single-product .single_add_to_cart_button,
.wpcf7-form input[type=submit]{
    line-height: 25px;
    padding: 31px 89px;
    color:#fff !important;
    font-size:13px;
      letter-spacing:2px;
      margin-top:40px;
      font-family:Lora;
      font-weight:400;
      width:311px;
      border:none;
      height:75px;
     background:url(https://xstore.8theme.com/demos/makeup/wp-content/uploads/sites/39/2017/06/pink-img.png) no-repeat;
}
.single-product .single_add_to_cart_button{
    line-height:18px;
}
.separatop-block{
    height:1px;
}
.separatop-block .vc_sep_line{
   border-color:#e3e3e3 !important;
}
.single-post .comment-form input[type="email"],
.single-post .comment-form input[type="text"],
.single-post .comment-form input[type="password"],
.single-post .comment-form textarea{
    border: none;
    border-bottom:1px solid #d9afbd;
}
input[type="text"]:focus, 
input[type="number"]:focus, 
input[type="email"]:focus, 
input[type="search"]:focus, 
input[type="password"]:focus, 
input[type="tel"]:focus, 
textarea:focus, 
textarea.form-control:focus {
    border-color: #9f3659 !important;
}

/*Blog*/
.blog .page-heading,
.single-post .page-heading {
    background-image: url(https://xstore.8theme.com/demos/makeup/wp-content/uploads/sites/39/2017/06/blog.jpg);
    background-position:center center;
}
.home .owl-pagination{
    margin-bottom:4vw;
}

.slide-view-timeline2 .meta-post-timeline,
.content-grid2 .meta-post-timeline{
    top:-2px !important;
}
.slider-container .wp-picture img{width:100%;}
.slider-container .blog-hover-animated,
.slider-container .grid-post-body{
    width:100% !important;
}
body .slider-container article.blog-post .read-more,
body .slider-container article.post-grid .read-more {
    display: block !important;
    margin-top: 10px;
    margin-bottom: 10px;
     display: table !important;
     color:#9f3659;
}

/*footer*/
.footer-bottom .textwidget img:hover{
    opacity:1 !important;
}
.footer-copyrights .textwidget,
.footer-copyrights .textwidget a{
    color:#464646;
    font-size:14px;
}
footer .block-follows  .et-follow-buttons a:hover i,
body .copyrights-widget .textwidget a:hover{
    color:#9f3659;
}

footer.footer .mpc-divider-wrap{
    display:none;
}
footer.footer .et-follow-buttons a i{
    color:#8d8c8c;
    margin-top:7px;
}

@media (min-width:482px) and (max-width:769px){
  body .footer.footer .footer-widgets .mpc-icon-column .mpc-icon{
      float:left !important;
      margin-right:5px !important;
  }
  body footer.footer .mpc-icon-column__content-wrap .mpc-icon-column__content .mpc-icon-column__description p{
      margin-top:-12px !important;
  }
  .block-follows{
      width:100%;
      padding-left:15px;
  }
  
}


@media only screen and (max-width:481px){
    .meta-post{
        font-size:15px !important;
    }
    .post-heading {
    margin-bottom: 1.2em !important;
}
body .resp-margin-top{
    margin-top:15px !important;
}
body .block-products .wpb_content_element.responsive-center{
    margin-top:-10px !important;
}
   footer.footer div,
    footer.footer p,
   .responsive-center,
   .block-follows .et-follow-buttons{
       text-align:center !important;
   }
   .woocommerce-breadcrumb{
     margin-bottom:0;
   }
   .woocommerce-variation-add-to-cart .single_add_to_cart_button{
      position:relative;
      left:-10px;
    }
    footer.footer{
        padding-top:40px;
    }
     body .page-heading{
        margin-bottom:20px;
        background-size:cover;
    }
    .single-product .page-heading{
        background-size:contain;
    }
     .block-follows .et-follow-buttons{
        padding-top:15px;
    }
    .wpcf7-form input[type=submit]{
        background-size:contain;
        padding:25px;
    }
    .custom-button{
          padding: 26px 59px;
    background-size: contain;
  }
  .button-see-all .custom-button{
      padding:26px 69px;
  }
  .section-home-galery .custom-button {
    padding: 24px 77px !important;
    background-size: contain;
}
    .responsiv-padding-bottom{
        padding-bottom:30px;
    }
    .responsiv-margin-top{
        margin-top:40px;
    }
    
    footer.footer .mpc-icon{
        margin-bottom:-5px !important;
    }
    body footer.footer  .mpc-icon-column__content .mpc-icon-column__description p{
        margin-bottom:20px !important;
    }
    .after-shop-loop{
        margin-bottom:40px;
    }
}
