/*====================*/
/*       Top bar      */
/*====================*/
.top-bar, .top-bar a, .top-bar .my-account-link>a, .top-bar .login-link a, .login-link label, .login-link p{
    text-transform: uppercase;
    font-size:12px;
}
/*====================*/

/*====================*/
/*     Quick View     */
/*====================*/
.quick-view-popup .quick-view-excerpts .excerpt-content:before {
    background: linear-gradient(to top, rgba(31, 31, 31, 0) 0%, #24102c 80%);
}
.archive.woocommerce-page.s_widgets-with-scroll .sidebar .sidebar-widget:not(.sidebar-slider) > ul:after, .archive.woocommerce-page.s_widgets-with-scroll .shop-filters .sidebar-widget:not(.sidebar-slider) > ul:after, .archive.woocommerce-page.s_widgets-with-scroll .sidebar .sidebar-widget:not(.sidebar-slider) > div:after, .archive.woocommerce-page.s_widgets-with-scroll .shop-filters .sidebar-widget:not(.sidebar-slider) > div:after, .quick-view-popup .quick-view-excerpts .excerpt-content:after {
    background: linear-gradient(to bottom, rgba(31, 31, 31, 0) 0%, #24102c 80%);
}
.quick-view-popup .product-name a {
    font-size: 30px;
}
/*====================*/

/*====================*/
/*       Shop         */
/*====================*/
.shop-banner {
    background: #42204d;
    border-right: 1px solid #684d71;
}
.shop-banner:last-child {
    border-right:none;
}
.shop-categories {
    margin: 0;
}
.category-description, .term-description, .filter-wrap, .page-description {
    margin-bottom: 25px;
}
/*====================*/

/*======================*/
/*     Single product   */
/*======================*/
.product-content .size-guide {
    padding: 10px 10px 10px 33px;
    background-position: 10px;
}
.single-product div.quantity {
    margin-bottom:15px;
}
.product-information-inner .product_title, .product-information-inner .title {
    letter-spacing: 6px;
    font-size: 26px;
}
 .yith-wcwl-add-button a.add_to_wishlist:hover, 
 .product-information .active-link:before {
     color:#ff83a0
 }
.single-product .images-wrapper {
    padding: 1.43em;
}
.woocommerce-Price-amount {
    font-family: Yantramanav !important;
    letter-spacing: 3px;
}
.product-information .woocommerce-Price-amount {
    font-size: 25px;
}
.product-information-inner {
    padding: 10%;
}
.single-product .type-product .product-content .product-information .loader-path {
    stroke: #888888;
}
/*======================*/

/*======================*/
/*         Blog         */
/*======================*/
.post-categories {
    display: none;
}
.archive .post-heading-inner .entry-title a, .blog .post-heading-inner .entry-title a {
    font-size: 30px;
    color: #ff8da7;
    letter-spacing: 5px;
}
.archive .post-comments-count span, .entry-date, .blog .post-comments-count span, .meta-post, .meta-post a, .post-comments-count,
.archive .read-more, .blog .read-more {
    color: #ffffff;
}
.archive .content-article p, .blog .content-article {
    color: #ac92b6;
}
.archive .read-more, .blog .read-more {
    text-align: center !important;
}
.single-post .post-header .post-heading {
    text-align: center;
    text-transform: uppercase;
}
.single-post .post-header .entry-title {
    font-size: 2.2em;
}
@media only screen and (min-width:1200px){
   .archive .content article:nth-child(odd) .post-data, .blog .content article:nth-child(odd) .post-data{
    text-align:center;
    padding:64px;
    border-radius:75px;
    background:#16061c;
    position:relative;
    left:-70px;
    z-index:99;
    display: inline-table;
    width: 610px;
}
.archive .content article:nth-child(even) .post-data, .blog .content article:nth-child(even) .post-data{
    text-align:center;
    padding:64px;
    border-radius:75px;
    background:#16061c;
    position:relative;
    right:-70px !important;
    z-index:99;
    display: inline-table;
    width: 610px;
}
}

/*==============================*/
@media only screen and (max-width: 767px){
body .testimonial-block .mpc-testimonial {
    padding: 25px 15px !important;
    margin: 20px auto 60px auto;
}
.mpc-interactive_image .mpc-hotspot__icon {
    font-size: 20px !important;
}
.responsive-center, .et-follow-buttons, .blog .content article .post-data {
    text-align: center !important;
}
}
